SET statement_timeout = 0;
SET client_encoding = 'UTF8';
SET standard_conforming_strings = on;
SET check_function_bodies = false;
SET client_min_messages = warning;

CREATE TABLE quote (
    id integer NOT NULL,
    quote text,
    author text,
    source text,
    created timestamp without time zone
);

CREATE SEQUENCE quote_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1;

ALTER SEQUENCE quote_id_seq OWNED BY quote.id;

ALTER TABLE ONLY quote ALTER COLUMN id SET DEFAULT nextval('quote_id_seq'::regclass);





SELECT pg_catalog.setval('quote_id_seq', 3561, true);

